import React, { useEffect, useState } from 'react';
import { getPokemons } from './IndexedDB.js'; // Assuming IndexedDB integration

const Favourites = () => {
  const [favouritePokemonList, setFavouritePokemonList] = useState([]);

  useEffect(() => {
    const fetchFavourites = async () => {
      const favouriteIds = await localStorage.getItem('favouritePokemonIds'); // Or IndexedDB
      if (favouriteIds) {
        const pokemonList = await Promise.all(
          favouriteIds.split(',').map(async (id) => {
            return await getPokemon(parseInt(id)); // Assuming getPokemon fetches from IndexedDB
          })
        );
        setFavouritePokemonList(pokemonList);
      }
    };
    fetchFavourites();
  }, []);

  return (
    <div className="container mx-auto p-4">
      {/* Search and error handling sections */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {pokemons
          .filter(pokemon => pokemon.name.toLowerCase().includes(searchQuery.toLowerCase()))
          .slice(0, listSize)
          .map(pokemon => (
            <Link
              key={pokemon.id}
              to={`/pokemon/${pokemon.id}`}
              className={`group bg-white shadow-md rounded p-4 hover:bg-gray-100 ${favouritePokemonIds.includes(pokemon.id.toString()) ? 'favourite' : ''}`} // Add favourite class based on state
            >
              <img src={pokemon.sprites.front_default} alt={pokemon.name} className="w-16 h-16 mx-auto mb-2" />
              <h3 className="text-xl font-bold text-center">{pokemon.name}</h3>
              <select
                className="block w-full mt-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                onChange={e => handleAddToFavourites(pokemon.id)} // Assuming handleAddToFavourites is defined
              >
                <option value="">Add to Favourites</option>
              </select>
              {/* Add more info like types, stats, etc. */}
            </Link>
          ))}
      </div>
  
      {/* Add pagination buttons if needed */}
    </div>
  );
};

export default Favourites;
