import React, { useEffect, useState } from 'react';
import { getPokemons } from '../indexedDB';

const Favourites = () => {
  const [favouritePokemonList, setFavouritePokemonList] = useState([]);

  useEffect(() => {
    const fetchFavourites = async () => {
      const favouriteIds = await getFromIndexedDB('favouritePokemonIds'); // Assuming getFromIndexedDB for IndexedDB
      if (favouriteIds) {
        const pokemonList = await Promise.all(
          favouriteIds.map(async (id) => {
            return await getPokemon(parseInt(id)); // Assuming getPokemon fetches from IndexedDB
          })
        );
        setFavouritePokemonList(pokemonList);
      }
    };
    fetchFavourites();
  }, []);

  return (
    <div className="container mx-auto p-4">
      {/* Search and error handling sections (you can implement these) */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {favouritePokemonList.map(pokemon => (
          <Link key={pokemon.id} to={`/pokemon/${pokemon.id}`} className="group bg-white shadow-md rounded p-4 hover:bg-gray-100">
            <img src={pokemon.sprites.front_default} alt={pokemon.name} className="w-16 h-16 mx-auto mb-2" />
            <h3 className="text-xl font-bold text-center">{pokemon.name}</h3>
            {/* Add more info like types, stats, etc. */}
          </Link>
        ))}
      </div>

      {/* Add pagination buttons if needed */}
    </div>
  );
};

export default Favourites;
