import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { getPokemons, savePokemon } from '../indexedDB';
import { SettingsContext } from './Settings';

function Home() {
  const [pokemons, setPokemons] = useState([]);
  const { searchQuery, listSize, setSearchQuery } = useContext(SettingsContext);
  const [isLoading, setIsLoading] = useState(false); // Track loading state
  const [error, setError] = useState(null); // Track any errors
  const [favouritePokemonIds, setFavouritePokemonIds] = useState([]); // Store favourite Pokemon IDs

  useEffect(() => {
    setIsLoading(true); // Set loading state on mount
    getPokemons().then(data => {
      if (data.length > 0) {
        setPokemons(data);
      } else {
        fetchAndCachePokemons();
      }
    }).finally(() => setIsLoading(false)); // Clear loading state after fetch
  }, []);

  const fetchAndCachePokemons = async () => {
    try {
      const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=150');
      const pokemonDetails = await Promise.all(response.data.results.map(async pokemon => {
        const detailResponse = await axios.get(pokemon.url);
        console.log('Fetched Pokémon detail:', detailResponse.data); // Log the detailed response
        await savePokemon(detailResponse.data);
        return detailResponse.data;
      }));
      setPokemons(pokemonDetails);
    } catch (error) {
      console.error('Failed to fetch Pokémon:', error);
      setError(error); // Set error state
    }
  };

  // Function to handle adding or removing Pokemon to Favourites
  const handleAddToFavourites = (pokemonId) => {
    const newFavourites = [...favouritePokemonIds];
    const isFavourite = newFavourites.includes(pokemonId.toString());

    if (isFavourite) {
      // Remove from favourites
      const filteredFavourites = newFavourites.filter(id => id !== pokemonId.toString());
      setFavouritePokemonIds(filteredFavourites);
    } else {
      // Add to favourites
      newFavourites.push(pokemonId.toString());
      setFavouritePokemonIds(newFavourites);
    }

    // Update IndexedDB (assuming an update function exists)
    updateFavouritePokemonIds(newFavourites); // Replace with your update function
  };

  useEffect(() => {
    const fetchFavouritesFromStorage = async () => {
      const ids = await getFromIndexedDB('favouritePokemonIds'); // Assuming getFromIndexedDB for IndexedDB
      setFavouritePokemonIds(ids ? ids : []);
    };
    fetchFavouritesFromStorage();
  }, []); // Run on component mount to fetch favourite IDs

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-4">
        <input
          type="text"
          placeholder="Search Pokémon"
          className="block w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          value={searchQuery}
          onChange={e => setSearchQuery(e.target.value)}
        />
        <button className="text-blue-500 hover:underline" onClick={() => setSearchQuery('')}>Clear</button>
      </div>
  
      {isLoading && <p className="text-center">Loading Pokémon...</p>}
      {error && <p className="text-center text-red-500">Error fetching Pokémon: {error.message}</p>}
  
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {pokemons
          .filter(pokemon => pokemon.name.toLowerCase().includes(searchQuery.toLowerCase()))
          .slice(0, listSize)
          .map(pokemon => (
            <Link key={pokemon.id} to={`/pokemon/${pokemon.id}`} className="group bg-white shadow-md rounded p-4 hover:bg-gray-100">
              <img src={pokemon.sprites.front_default} alt={pokemon.name} className="w-16 h-16 mx-auto mb-2" />
              <h3 className="text-xl font-bold text-center">{pokemon.name}</h3>
              <select
                value={favouritePokemonIds.includes(pokemon.id.toString()) ? 'favourite' : ''}
                onChange={e => handleAddToFavourites(pokemon.id)}
                className="block w-full mt-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="">Add to Favourites</option>
                <option value="favourite">Remove from Favourites</option>
              </select>
              {/* Add more info like types, stats, etc. */}
            </Link>
          ))}
      </div>
  
      {/* Add pagination buttons if needed */}
    </div>
  );
};

export default Home;
